@js:
(() => {
    let praseResult = [];
    let Roads = xpath(result, "//a[@data-toggle]/text()");
    let ChapterList = xpath(result, "//div[@class=\"tab-content myui-panel_bd\"]/div/*");
    console.log(Roads);console.log(ChapterList.length);

    for (let index = 0; index < ChapterList.length; index++) {
        let item_road = [];
        let list = (xpath(ChapterList[index], "(//ul/li//a"));
        for (let j = 0; j < list.length; j++) {
            let title = (xpath(list[j], "//a/text()")).join('');
            let url = (xpath(list[j], "//a/@href")).join('');
            item_road.push({
                'title': title,
                'url': url,
            });
        }
        praseResult.push({
            'roadName': Roads[index],
            'items': item_road,
        });
    }
    //print(JSON.stringify(praseResult));
    return praseResult;
})()

